prefix ex:    <http://example.org/>
prefix st:    <http://example.org/stigld/>
prefix rdf:   <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
prefix xsd:   <http://www.w3.org/2001/XMLSchema#>
prefix en:    <http://example.org/entities#>
######################PERFORM PRODUCTION####################################
DELETE {
    ?task ?p1 ?o .
    ?s ?p2 ?task .
    ?order ?p_o ?o_o
}
INSERT {
    ?id a ex:Product ;
    ex:located ?target ;
    ex:created ?now .

    ?target st:carries ?marker .
    ?marker a st:Stigma, ex:TransportStigma ; st:created ?now ;
      st:decayRate "0.05"^^xsd:double ; st:level "1000.0"^^xsd:double .
}
WHERE {
  BIND (NOW() as ?now)
  { SELECT * WHERE {
      ?machine ex:queue ?task .
      { SELECT (UUID() as ?id) (BNODE() as ?marker) ?task WHERE {
          ?task a ex:WorkstationTask ; ex:endTime ?endTime .
          FILTER(?endTime < NOW())}}
  }}

  {SELECT (COUNT(?done) as ?waiting) ?output WHERE {
    ?output a ex:Port ; ex:located ?pos .
    OPTIONAL {?done a ex:Product ; ex:located ?pos .}
  } GROUP BY ?output}

  ?machine a ex:ProductionArtifact ; ex:queue ?task ; ex:outputPort ?output .
  ?output ex:capacity ?capacity ; ex:located ?target.

  OPTIONAL { ?task ?p1 ?o . }
  OPTIONAL { ?s ?p2 ?task . }

  ?task ex:order ?order .
  ?order ?p_o ?o_o .

  FILTER(?waiting < ?capacity)
};
##################################### PERFORM MOVE ##########################################
DELETE {
  ?t ex:located ?from ; ex:status ex:busy ; ex:queue ?task .
  ?task a ex:MoveTask ; ex:startTime ?start ; ex:endTime ?end ; ex:toLocation ?to .
}
INSERT {
  ?t ex:located ?finalTO ; ex:status ex:idle .
}
WHERE {
  ?t a ex:Transporter ; ex:located ?from ; ex:queue ?task; ex:idlePosition ?idle.
  ?task a ex:MoveTask ; ex:startTime ?start ; ex:endTime ?end ; ex:toLocation ?to .
  OPTIONAL {?t1 a ex:Transporter; ex:located ?to}
  BIND(IF(BOUND(?t1), ?from, ?to) as ?finalTO)
  FILTER (isBlank(?task))
  FILTER (NOW() > ?end )
};
##############################ASSIGN MOVE########################################
DELETE {
  ?t ex:status ex:idle .
}
INSERT {
  ?t ex:queue ?task  ; ex:status ex:busy .
  ?task a ex:MoveTask ; ex:startTime ?now ; ex:endTime ?endTime ; ex:toLocation ?to .
}
WHERE {
  SELECT (BNODE() as ?task) ?now ?endTime ?t ?from ?to WHERE {

    ?t a ex:Transporter ; ex:located ?from ; ex:speed ?speed ; ex:status ex:idle  .
    {SELECT (SAMPLE(?adj) as ?to) ?t WHERE {
      ?t ex:located ?from .
      {SELECT ?adj ?from WHERE {
        ?from st:adjacentTo ?adj.
        ?adj st:carries [ a ex:TransportStigma ; st:level ?lvl ].
        FILTER NOT EXISTS {
          ?from st:adjacentTo ?otherAdj.
          ?otherAdj st:carries [ a ex:TransportStigma ; st:level ?otherLvl ].
          FILTER(?otherLvl > ?lvl) }
      }}
    } GROUP BY ?t}

    FILTER NOT EXISTS {
      ?m ex:located ?from ; a ex:Port .
    }

    BIND (NOW() as?now)
    BIND (?now + "PT10S"^^xsd:duration * (1/?speed) as ?endTime)
}} ;


DELETE {
  ?t ex:status ex:idle .
}
INSERT {
  ?t ex:queue ?task  ; ex:status ex:busy .
  ?task a ex:MoveTask ; ex:startTime ?now ; ex:endTime ?endTime ; ex:toLocation ?to .
}
WHERE {
  SELECT (BNODE() as ?task) ?now ?endTime ?t ?from ?to WHERE {
  ?from st:adjacentTo ?to.
  ?to st:carries [ a ex:DiffusionTrace ; st:level ?lvl] .
  ?t a ex:Transporter ; ex:located ?from ; ex:speed ?speed ; ex:status ex:idle  .
  {SELECT (SAMPLE(?adj) as ?to) ?t WHERE {
    ?t ex:located ?from .
    {SELECT ?adj ?from WHERE {
      ?from st:adjacentTo ?adj.
      ?adj st:carries [ a ex:DiffusionTrace ; st:level ?lvl ].
      FILTER NOT EXISTS  {?something a ex:Artifact ; ex:located ?adj .}
      FILTER NOT EXISTS {
        ?from st:adjacentTo ?otherAdj.
        FILTER NOT EXISTS  {?something a ex:Artifact ; ex:located ?otherAdj .}
        ?otherAdj st:carries [ a ex:DiffusionTrace ; st:level ?otherLvl ].
        FILTER(?otherLvl > ?lvl) }
    }}
  } GROUP BY ?t}

  FILTER NOT EXISTS {
    ?m ex:located ?from ; a ex:Port .
  }
  BIND (NOW() as?now)
  BIND (?now + "PT10S"^^xsd:duration * (1/?speed) as ?endTime)
}};
##################################### CREATE PICKUP #########################################
DELETE {
  ?t ex:status ex:idle .
}
INSERT {
  ?t ex:queue ?task  ; ex:status ex:busy .
  ?task a ex:PickUpTask ; ex:startTime ?now ; ex:endTime ?endTime  .
}
WHERE {
{SELECT (BNODE() as ?task) ?now ?endTime ?t ?factor WHERE {
  BIND (NOW() as?now)
  ?t a ex:Transporter ; ex:located ?from ; ex:speed ?speed ; ex:status ex:idle  .
  {SELECT (COUNT(?product) as ?count) ?from WHERE{
    ?port ex:located ?from; a ex:Port .
    ?product ex:located ?from; a ex:Product .
  } GROUP BY (?from)}
  BIND (?now + "PT20S"^^xsd:duration*(?count) as ?endTime)
}}};
###################################### FINISH PICKUP #############################################
DELETE {
 ?transporter ex:status ?status ; ex:located ?topos ; ex:queue ?task .
 ?product a ex:Product ; ex:located ?topos ; ?p ?x .
 ?task a ex:PickUpTask ; ex:endTime ?endTime ; ex:startTime ?startTime .
 ?topos st:carries ?stigma .
 ?stigma a st:Stigma , ex:TransportStigma ; ?stig_p ?stig_o .
 ?all_that st:carries ?trace .
 ?trace a st:Stigma , ex:DiffusionTrace ; ex:diffusionSource ?topos ; ?trace_p ?trace_o .
}
INSERT {
  ?transporter ex:status ex:idle ; ex:located ?idlePosition .
}
WHERE {
  ?task a ex:PickUpTask ; ex:endTime ?endTime ; ex:startTime ?startTime .
  ?transporter a ex:Transporter ; ex:status ?status ;
    ex:located ?topos ; ex:idlePosition ?idlePosition ; ex:queue ?task .

  ?product a ex:Product ; ex:located ?topos ; ?p ?x .
  ?stigma a st:Stigma , ex:TransportStigma ; ?stig_p ?stig_o .
  ?topos st:carries ?stigma .
  ?trace a st:Stigma , ex:DiffusionTrace; ?p ?o ; ex:diffusionSource ?topos ; ?trace_p ?trace_o .
  ?all_that st:carries ?trace .
  FILTER (isBlank(?stigma))
  FILTER (isBlank(?trace))
  FILTER(now() > ?endTime)
}
